import React, { useState, useEffect, Fragment} from 'react';
import { useLocation } from 'react-router-dom';
import config from './../config'

export const Show = () => {
  const location = useLocation();
  const [history, setHistory] = useState([]);
  const contact = location.state;
  // const [showHistory, setShowHistory] = useState(false);

  const fetchContactHistory = async () => {
    const res = await fetch(`${config.API_URL}/contacts/${location.state.id}/history`);
    const data = await res.json();
    let changed = [];
    if(data){
      data.map((history) => (
        changed.push(parseJSON(history.changes))
      ))
    }

    setHistory(changed);
  };

  useEffect(() => {
    fetchContactHistory();
  }, [])

  console.log("Show component receivedData:", location.state);

  const parseJSON = (changes) => {
    const val = JSON.parse(changes)
    const firstKey = Object.keys(val)[0];
    return val[firstKey]
  }

  return (
    // <div className="p-6 max-w-4xl mx-auto">
    //   <h1 className="text-2xl font-bold mb-4">Contact History</h1>
    //   <div className="grid gap-4">
    //     {contact.first_name}
    //     {contact.last_name}
    //     {contact.email}
    //     {contact.phone_number}
    //   </div>

    //   <div className="grid gap-4">
    //     {console.log('history :>> ', history)}
    //     {history.map((h) => (
    //       <Fragment key={h}>
    //         <p> <b>From: </b>{h?.from} <b>To: </b> {h?.to} </p>
    //       </Fragment>
    //     ))}
    //   </div>
    // </div>

<div className="p-6 max-w-4xl mx-auto">
      <div class="bg-white max-w-2xl shadow overflow-hidden sm:rounded-lg">
          <div class="px-4 py-5 sm:px-6">
              <h3 class="text-lg leading-6 font-medium text-gray-900">
                {contact.first_name} {contact.last_name}
              </h3>
              <p class="mt-1 max-w-2xl text-sm text-gray-500">
                <b> Email: </b> {contact.email} 
              </p>
              <p class="mt-1 max-w-2xl text-sm text-gray-500">
                <b> Phone Number: </b> {contact.phone_number} 
              </p>
          </div>
          <div class="border-t border-gray-200">
            <dl>
              <div class="bg-gray-50 px-4 py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6">
                <h1> <u> Changes history </u> </h1>
                <br/>
                    {history && history.map((h, i) => (     
                      <dd key={i}class="mt-1 text-sm text-gray-900 sm:mt-0 sm:col-span-2"> 
                        <b>From: </b> {h?.from} | <b>To: </b> {h?.to}
                      </dd>
                    ))}
                  </div>
              </dl>
          </div>
      </div>
</div>
  );
}
