import { useEffect, useState } from "react";
import { useNavigate, Link } from 'react-router-dom';

const API_URL = "http://localhost:3000/contacts"; // Change this to your backend URL

export const Main = () => {
  const [contacts, setContacts] = useState([
    {id: 1, first_name: "Russell", last_name: "G", email: "f1ash.tm1990@gmail.com", phone_number: "1234567890"},
    {id: 2, first_name: "Russell", last_name: "G", email: "f1ash.tm1990@gmail.com", phone_number: "1234567890"},
    {id: 3, first_name: "Russell", last_name: "G", email: "f1ash.tm1990@gmail.com", phone_number: "1234567890"},
  ]);

  const [form, setForm] = useState({ first_name: "", last_name: "", email: "", phone_number: "" });


  const fetchContacts = async () => {
    const res = await fetch(API_URL);
    const data = await res.json();
    setContacts(data);
  };

  useEffect(() => {
    fetchContacts();
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const method = editing ? "PUT" : "POST";
    const url = editing ? `${API_URL}/${editing.id}` : API_URL;
    await fetch(url, {
      method,
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(form),
    });
    setForm({ first_name: "", last_name: "", email: "", phone_number: "" });
    setEditing(null);
    fetchContacts();
  };

  const handleDelete = async (id) => {
    await fetch(`${API_URL}/${id}`, { method: "DELETE" });
    fetchContacts();
  };

  const handleEdit = (contact) => {
    setForm(contact);
    setEditing(contact);
  };

  const handleViewHistory = async (id) => {
    const res = await fetch(`${API_URL}/${id}/history`);
    const data = await res.json();
    setHistory(data);
    setShowHistory(true);
  };

  const navigate = useNavigate();

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Contacts</h1>
      <div className="grid gap-4">

      <div className="relative overflow-x-auto">
        <table className="w-full text-sm text-left rtl:text-right text-gray-500 ">
            <thead className="text-xs text-gray-700 uppercase bg-gray-50 dark:bg-gray-700 dark:text-gray-400">
                <tr>
                    <th scope="col" className="px-6 py-3">
                        Product name
                    </th>
                    <th scope="col" className="px-6 py-3">
                        Color
                    </th>
                    <th scope="col" className="px-6 py-3">
                        Category
                    </th>
                    <th scope="col" className="px-6 py-3">
                        Price
                    </th>
                </tr>
            </thead>
            <tbody>
            {contacts.map((contact) => (
              <>  
                <tr className="bg-white border-b" onClick={() => navigate(`/contacts/${contact.id}`, { state: contact }) }>
                    <td scope="row" className="px-6 py-4">
                        Russell
                    </td>
                    <td className="px-6 py-4">
                        G
                    </td>
                    <td className="px-6 py-4">
                        f1ash.tm1990@gmail.com
                    </td>
                    <td className="px-6 py-4 font-medium text-gray-900 whitespace-nowrap">
                        1234567890
                    </td>
                </tr>
              </>
            ))}
            </tbody>
        </table>
    </div>

      </div>
    </div>
  );
}
