import React, { useState, useEffect} from 'react';
import { useLocation } from 'react-router-dom';
import config from './../config'

export const Show = () => {
  const location = useLocation();
  const [history, setHistory] = useState([]);
  // const [showHistory, setShowHistory] = useState(false);

  const fetchContactHistory = async () => {
    const res = await fetch(`${config.API_URL}/contacts/${4}/history`);
    const data = await res.json();

    // let data = [
    //   {id: 1, first_name: "Russell", last_name: "G", email: "f10@gmail.com", phone_number: "1234567890"},
    //   {id: 2, first_name: "Russell", last_name: "G", email: "f10@gmail.com", phone_number: "1234567890"},
    //   {id: 3, first_name: "Russell", last_name: "G", email: "f10@gmail.com", phone_number: "1234567890"}
    // ]; 
    console.log(data);
    setHistory(data);
  };

  useEffect(() => {
    fetchContactHistory();
  }, [])


  console.log("Show component receivedData:", location.state);

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Contact History</h1>
      <div className="grid gap-4">
        history
      </div>
    </div>
  );
}
