import React, { useState, useEffect, Fragment} from 'react';
import { useLocation } from 'react-router-dom';
import config from './../config'

export const Show = () => {
  const location = useLocation();
  const [history, setHistory] = useState([]);
  const contact = location.state;
  // const [showHistory, setShowHistory] = useState(false);

  const fetchContactHistory = async () => {
    const res = await fetch(`${config.API_URL}/contacts/${location.state.id}/history`);
    const data = await res.json();
    console.log("data ", data);
    let changed = [];
    data.map((history) => (
      changed.push(parseJSON(history.changes))
    ))

    setHistory(changed);
  };

  useEffect(() => {
    fetchContactHistory();
  }, [])

  console.log("Show component receivedData:", location.state);

  const parseJSON = (changes) => {
    const val = JSON.parse(changes)
    const firstKey = Object.keys(val)[0];
    return val[firstKey]
  }

  return (
    // <div className="p-6 max-w-4xl mx-auto">
    //   <h1 className="text-2xl font-bold mb-4">Contact History</h1>
    //   <div className="grid gap-4">
    //     {contact.first_name}
    //     {contact.last_name}
    //     {contact.email}
    //     {contact.phone_number}
    //   </div>

    //   <div className="grid gap-4">
    //     {console.log('history :>> ', history)}
    //     {history.map((h) => (
    //       <Fragment key={h}>
    //         <p> <b>From: </b>{h?.from} <b>To: </b> {h?.to} </p>
    //       </Fragment>
    //     ))}
    //   </div>
    // </div>
 <div class="relative flex flex-col my-6 bg-white shadow-sm border border-slate-200 rounded-lg w-96 p-2">
    <div class="w-full">
      <ul class="relative flex flex-wrap px-1.5 py-1.5 list-none rounded-md bg-slate-100" data-tabs="tabs" role="list">
        <li class="z-30 flex-auto text-center">
          <a class="flex items-center justify-center w-full px-0 py-2 text-sm mb-0 transition-all ease-in-out border-0 rounded-md cursor-pointer text-slate-600 bg-inherit"
          data-tab-target="" role="tab" aria-selected="true" aria-controls="dashboard">
            Dashboard
          </a>
        </li>
        <li class="z-30 flex-auto text-center">
          <a class="flex items-center justify-center w-full px-0 py-2 mb-0 text-sm transition-all ease-in-out border-0 rounded-lg cursor-pointer text-slate-600 bg-inherit"
          data-tab-target="" role="tab" aria-selected="false"  aria-controls="profile">
            Profile
          </a>
        </li>
      <li class="z-30 flex-auto text-center">
          <a class="flex items-center justify-center w-full px-0 py-2 text-sm mb-0 transition-all ease-in-out border-0 rounded-lg cursor-pointer text-slate-700 bg-inherit"
          data-tab-target="" role="tab" aria-selected="false" aria-controls="settings">
            Settings
          </a>
        </li>
      </ul>

      <div data-tab-content="" class="p-3">
        <div id="dashboard" role="tabpanel">
          <h5 class="mb-2 text-slate-800 text-xl font-semibold">
            Website Review Check Text
          </h5>
          <p class="text-slate-600 leading-normal font-light">
            Because it&apos;s about motivating the doers. Because I&apos;m
            here to follow my dreams and inspire other people to follow their
            dreams, too.
          </p>
        </div>
        <div class="hidden opacity-0" id="profile" role="tabpanel">
          <h5 class="mb-2 text-slate-800 text-xl font-semibold">
            The place is close to Barceloneta
          </h5>
          <p class="text-slate-600 leading-normal font-light">
            The reading of all good books is like a conversation with the
            finest minds of past centuries. Follow my dreams and inspire other people.
          </p>
        </div>
        <div class="hidden opacity-0" id="settings" role="tabpanel">
          <h5 class="mb-2 text-slate-800 text-xl font-semibold">
            Close to Barceloneta
          </h5>
          <p class="text-slate-600 leading-normal font-light">
            Comparing yourself to others is the thief of joy. 
          The reading of all good books is like a conversation
          </p>
        </div>
      </div>
    </div>
</div>
  );
}
