import React, { useState, useEffect, Fragment} from 'react';
import { useLocation } from 'react-router-dom';
import config from './../config'

export const Show = () => {
  const location = useLocation();
  const [history, setHistory] = useState([]);
  const contact = location.state;
  // const [showHistory, setShowHistory] = useState(false);

  const fetchContactHistory = async () => {
    const res = await fetch(`${config.API_URL}/contacts/${location.state.id}/history`);
    const data = await res.json();
    console.log("data ", data);
    let changed = [];
    data.map((history) => (
      changed.push(parseJSON(history.changes))
    ))

    setHistory(changed);
  };

  useEffect(() => {
    fetchContactHistory();
  }, [])

  console.log("Show component receivedData:", location.state);

  const parseJSON = (changes) => {
    const val = JSON.parse(changes)
    const firstKey = Object.keys(val)[0];
    return val[firstKey]
  }

  return (
