import { Fragment, useEffect, useState } from "react";
import { useNavigate, Link } from 'react-router-dom';
import config from './config'


export const Main = () => {
  const navigate = useNavigate();
  const [contacts, setContacts] = useState([]);

  const fetchContacts = async () => {
    const res = await fetch(API_URL+"/contacts");
    const data2 = await res.json();

    // let data = [
    //   {id: 1, first_name: "Russell", last_name: "G", email: "f10@gmail.com", phone_number: "1234567890"},
    //   {id: 2, first_name: "Russell", last_name: "G", email: "f10@gmail.com", phone_number: "1234567890"},
    //   {id: 3, first_name: "Russell", last_name: "G", email: "f10@gmail.com", phone_number: "1234567890"}
    // ]; 
    // console.log(data);
    setContacts(data2);
  };

  useEffect(() => {
    fetchContacts();
  }, []);

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Contacts</h1>
      <div className="grid gap-4">

      <div className="relative overflow-x-auto">
        <table className="w-full text-sm text-left rtl:text-right text-gray-500 ">
            <thead className="text-xs text-gray-700 uppercase bg-gray-50 dark:bg-gray-700 dark:text-gray-400">
                <tr>
                    <th scope="col" className="px-6 py-3">
                        Firstname
                    </th>
                    <th scope="col" className="px-6 py-3">
                        Lastname
                    </th>
                    <th scope="col" className="px-6 py-3">
                        Email
                    </th>
                    <th scope="col" className="px-6 py-3">
                        Phone Number
                    </th>
                </tr>
            </thead>
            <tbody>
            {contacts.map((contact) => (
              <Fragment key={contact.id}>
                <tr className="bg-white border-b" onClick={() => navigate(`/contacts/${contact.id}`, { state: contact }) }>
                    <td scope="row" className="px-6 py-4">
                        {contact.first_name}
                    </td>
                    <td className="px-6 py-4">
                        {contact.last_name}
                    </td>
                    <td className="px-6 py-4">
                        {contact.email}
                    </td>
                    <td className="px-6 py-4 font-medium text-gray-900 whitespace-nowrap">
                        {contact.phone_number}
                    </td>
                </tr>
                </Fragment>
            ))}
            </tbody>
        </table>
    </div>

      </div>
    </div>
  );
}
